package com.adobe.core.hootsuite.integration.external.services.exceptions;

import com.adobe.core.hootsuite.integration.exterrnal.exceptions.BaseException;

/**
 * 
 * This is the business exception for scenarios like;
 * <ul>
 * 		<li>OAuth Token could not generated by the OAuth Server, OKTA.</li>
 * 		<li>Cloud configuration was missing or invalid in the input params.</li>
 * </ul>
 * 
 *
 * @version 1.0
 * @since 2020.04.15
 * 
 * ******************************************************************************
 *          		Version History
 * ******************************************************************************
 * 
 *          1.0 					Initial Version
 * 
 * *******************************************************************************
 */
public class OAuthException extends BaseException {

	/** The Constant serialVersionUID. */
	private static final long serialVersionUID = 7524642528026692193L;

	/**
	 * Instantiates a new OAuth exception.
	 *
	 * @param errors the errors
	 * @param e {@link Throwable} the e
	 */
	public OAuthException(String errors, Throwable e) {
		super(errors, e);
	}

	/**
	 * Parameterized constructor. Takes exception message, code and object as
	 * argument
	 * 
	 * @param message exception message.
	 * @param eCode   exception error code
	 */
	public OAuthException(String message, String eCode) {
		super(message, eCode);
	}
}
